    export default {
        name: "Deduction",
        data() {
            return {
                tabIndex: 0,
                deductionT: 1,
                deduction: {
                    de_9001_001: null,
                    de_9001_002: null,
                    de_9001_003: null,
                    de_9008_001: null,
