                    de_9008_003: null,
                    de_9008_004: null,
                },
                creditT: 1,
                insuT:1,
                plans: [],
                credit: {
                    cr_9312_001: null,
                    cr_93121_001:null,
                    cr_93121_002:null,
                    cr_93121_003:null,
                }
            }
        },
        methods: {
            de_next(addition) {
                this.deductionT = addition;
            },
            formsubmit(e) {
                let self = this;
                e.preventDefault();
                axios.post('/api/deductionInfo', {
                    de_9001_001: this.deduction.de_9001_001,
                    de_9001_002: this.deduction.de_9001_002,
                    de_9001_003: this.deduction.de_9001_003,
                    de_9008_001: this.deduction.de_9008_001,
                    de_9008_002: this.deduction.de_9008_002,
                    de_9008_003: this.deduction.de_9008_003,
                    de_9008_004: this.deduction.de_9008_004,
                }).then(function (response) {
                    self.$router.push('/credit');
                    alert('success')
                }).catch(function (error) {
                    alert(error)
                })
            },
                        newplan() {
                this.plans.push({ cr_9312_002: '',  cr_9312_003: '', cr_9312_004: ''})
            },
            deleteplan(index) {
                this.plans.splice(index, 1)
            },
            cr_next(addition) {
                this.creditT = addition;
            },
            insur_next(addition) {
                this.insuT = addition;
            },
            formSubmit(e) {
                e.preventDefault();
                let self = this;
                console.log(this.plans);
                axios.post('/api/creditInfo', {
                    // cr_9004_001: this.credit.cr_9004_001,
                    // cr_9004_002: this.credit.cr_9004_002,
                    // cr_9004_003: this.credit.cr_9004_003,
